mapper.insert:
		INSERT INTO MOVIE(
			  NO
			, TITLE
			, DIRECTOR
			, ACTOR
			, RELEASEDATE
			, RUNNINGTIME
			, AGE
			, COUNTRY
			, INFO
			, PROFILEIMG
 			, GENRE
 			, ENDDATE
		)
		VALUES (
			  MOVIE_SEQ.NEXTVAL
			, #{title}
			, #{director}
			, #{actor}
			, #{releaseDate}
			, #{runningTime}
			, #{age}
			, #{country}
			, #{info}
			, #{profileImg}
			, #{genre}
			, #{endDate}
		)

